def fibonacci_sequence(n):
    sequence = []
    a, b = 0, 1
    while a < n:
        sequence.append(a)
        a, b = b, a+b
    return sequence

try:
    num = int(input("Enter a number: "))
    print(fibonacci_sequence(num))
except ValueError:
    print("Invalid input! Please enter a number.")
